catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '' || g_form.getValue('currentSet') == '') {
      return;
   }
	var current_set_str = g_form.getValue('currentSet').toString();
	var current_set = current_set_str.evalJSON();

	if(g_form.getValue('phase') == 'Modify' && newValue != current_set.hardwarepreset){
		control.style.backgroundColor = 'yellow';
		control.style.color = 'purple';
		console.log(newValue+" : "+current_set.hardwarepreset);
		return;
	}else if(g_form.getValue('phase') == 'Modify' && newValue == current_set.hardwarepreset){
		control.style.backgroundColor = '#FFF';
		control.style.color = '#343D47';
		console.log("new value "+newValue+" : "+current_set.hardwarepreset);
		return;
	}


   //Type appropriate comment here, and begin script below

}
