catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

  console.log("ebnable Backup: "+ newValue);

	if(newValue == 'No' )
		{

			g_form.setDisplay('lbVIP8AppName',false);
			g_form.setDisplay('lbVIP8Url',false);
			g_form.setDisplay('lbVIP8Mode',false);
			g_form.setDisplay('lbVIP8Port',false);
			g_form.setDisplay('lbVIP8Method', false);

		}

	if(newValue == 'Yes' &&  g_form.getValue('phase') != 'Action' && g_form.getValue('phase') != 'Decommission')
		{

			g_form.setDisplay('lbVIP8AppName',true);
			g_form.setDisplay('lbVIP8Url',true);
			g_form.setDisplay('lbVIP8Mode',true);
			g_form.setDisplay('lbVIP8Port',true);
			g_form.setDisplay('lbVIP8Method', true);

		}


}
