catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

  console.log("ebnable Backup: "+ newValue);

	if(newValue == 'No' )
		{

			g_form.setDisplay('lbVIP6AppName',false);
			g_form.setDisplay('lbVIP6Url',false);
			g_form.setDisplay('lbVIP6Mode',false);
			g_form.setDisplay('lbVIP6Port',false);
			g_form.setDisplay('lbVIP6Method', false);

		}

	if(newValue == 'Yes' &&  g_form.getValue('phase') != 'Action' && g_form.getValue('phase') != 'Decommission')
		{

			g_form.setDisplay('lbVIP6AppName',true);
			g_form.setDisplay('lbVIP6Url',true);
			g_form.setDisplay('lbVIP6Mode',true);
			g_form.setDisplay('lbVIP6Port',true);
			g_form.setDisplay('lbVIP6Method', true);

		}


}
