catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '' || g_form.getValue('currentSet') == '') {
      return;
   }
	var current_set_str = g_form.getValue('currentSet').toString();
	var current_set = current_set_str.evalJSON();
	if(g_form.getValue('phase') == 'Modify' && newValue != current_set.backupwindow){
		control.style.backgroundColor = 'yellow';
		control.style.color = 'purple';
		console.log("Change disk 1 : "+newValue+" : "+current_set.backupwindow);
		return;
	}else if(g_form.getValue('phase') == 'Modify' && newValue == current_set.backupwindow){
		control.style.backgroundColor = '#FFF';
		control.style.color = '#343D47';
		console.log("back to "+newValue+" : "+current_set.backupwindow);
		return;
	}

}
