var polling_activity_no = 14;
workflow.info("data.get("+polling_activity_no+").statucode : "+data.get(polling_activity_no).status_code);
workflow.info("resultStatusName : "+data.get(polling_activity_no).output.executionSummary.resultStatusName);
workflow.info("resultStatusType : "+data.get(polling_activity_no).output.executionSummary.resultStatusType);
workflow.info("es_status : "+data.get(polling_activity_no).output.executionSummary.status);
workflow.info("Responce Body : "+data.get(polling_activity_no).body);

responseBodystr = data.get(polling_activity_no).body;
responsebody = JSON.parse(responseBodystr);
workflow.info(responsebody.executionSummary.resultStatusType);
workflow.info(responsebody.executionSummary.resultStatusName);
workflow.info(responsebody.executionSummary.status);


if(data.get(polling_activity_no).status_code==200)
	{
	
	workflow.info("Current status is = "+ responsebody.executionSummary.status);
	workflow.scratchpad.executionstatus =   responsebody.executionSummary.status;
	
	if(responsebody.executionSummary.status === "COMPLETED")
		{
		workflow.info('completed with Success'+ responsebody.executionSummary.status);
		activity.result = 'success';
		
		workflow.scratchpad.executionstatus = responsebody.executionSummary.status;
		
	}
	else if(responsebody.executionSummary.status === "RUNNING")
		{
		
		activity.result = 'running';
		workflow.info('Deploy flow is running on ucCore');
	}
	else if(responsebody.executionSummary.status === "PAUSED"){
		activity.result = 'running';
		workflow.info('activity is paused due to pasued OO flow');
		
		
	}
	else {
		workflow.info("Current status is completed with failure" );
		activity.result = 'error';
		workflow.error('completed with failure error');
	}
	
	
	
}
else
	{
	activity.result = 'error';
	workflow.info("Failed to create vm. HTTP Error Code : "+ response.getErrorMessage());
	workflow.error('failure error');
}

