workflow.info("responsebody.executionId : "+responsebody.executionId);


workflow.scratchpad.getvminfoexecutionId = responsebody.executionId;

var get_vm_info_activity_no = 17;
workflow.info("data.get("+get_vm_info_activity_no+").statucode : "+data.get(get_vm_info_activity_no).status_code);
//workflow.info("es_status : "+data.get(get_vm_info_activity_no).output.executionSummary.status);
